import {Component, Input} from '@angular/core'
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  @Input() flights!: Array<any>;
  @Input() locations!: Array<string>;
  title = 'frontend';

  receiveListOfFlights($event: Array<any>){
    this.flights = $event;
  }
  receiveLocations($event: Array<string>){
    this.locations = $event;
  }
}
<app-browser-form (flightsSender)="receiveListOfFlights($event)" (LocationsSender)="receiveLocations($event)"></app-browser-form>
@for (flight of flights; track flight.id){
<!--  {{flight.price.total}}-->
  <app-flight-record [flightRecord]="flight" loca></app-flight-record>
}
