import {Component, Input} from '@angular/core'
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  @Input() flights: Array<any>  = [];
  @Input() locations!: string[];
  @Input() isSubmitted = false;
  receiveListOfFlights($event: Array<any>){
    this.flights = $event;
  }
  receiveLocations($event: string[]){
    this.locations = $event;
  }
  receiveFormStatus($event: boolean){
    this.isSubmitted = $event;
  }
}
<div class="app-container">
  <app-browser-form (flightsSender)="receiveListOfFlights($event)" (locationsSender)="receiveLocations($event)" (submitStatus)="receiveFormStatus($event)"></app-browser-form>
  <div class="available-flights-container">
    @for (flight of flights; track flight.id){
    <app-flight-record [flightRecord]="flight" [locations]="locations"></app-flight-record>
    }
  </div>
  @if(flights.length === 0 && isSubmitted){
    <div class="loading">
      <mat-spinner></mat-spinner>
      <div>Searching for flights...</div>
    </div>
  }

</div>
