import {Component, Input} from '@angular/core';
import {FlightDetailsModel} from "../../models/flight-details.model";

@Component({
  selector: 'app-flight-record',
  templateUrl: './flight-record.component.html',
  styleUrls: ['./flight-record.component.scss']
})
export class FlightRecordComponent {
  @Input() flightRecord!:FlightDetailsModel;
  @Input() locations!:string[];
}
<div class="main-container">
 <div class="info-container">
   @for(flightDetails of flightRecord.itineraries;track flightDetails.segments){
     <div class="container">
       <i class="fa-solid fa-plane-departure"></i>
     <div class="flight-time">
       <b>
         {{(flightDetails.segments[0].departure.at).toLocaleString() | date: "H:mm" }}
       </b>
       <p class="bigger-text">
<!--         {{locations[0]}}-->
         {{flightDetails.segments[0].departure.iataCode}}
       </p>
     </div>
       <div class="flight-time">
         <i class="fa-solid fa-arrow-right-long"></i>
        <p>
          {{(flightDetails.duration).toString().slice(2)}}
        </p>
       </div>
       <i class="fa-solid fa-plane-arrival"></i>
       <div class="flight-datetime">
       <b>
         {{(flightDetails.segments[flightDetails.segments.length-1].arrival.at).toLocaleString() | date: "H:mm" }}
       </b>
       <p>
<!--         {{locations[1]}}-->
         {{flightDetails.segments[flightDetails.segments.length-1].arrival.iataCode}}
       </p>
       </div>
     </div>
   }
 </div>
 <div class="price-container">
   <p class="price-title">Price</p>
   <p>{{flightRecord.price.total}} {{flightRecord.price.currency}}</p>
 </div>
</div>
